

const JsonLog = (a: any) => console.log(JSON.stringify(a));
const i1 = ""

const decodeInput = (data: string ) => {
    let step = 10;
    const result: { methodSig: string, args: string[] } = {
        methodSig: data.slice(0, step),
        args: new Array<string>(),
    };
    let currentArg: string = data.slice(step, step + 64);
    while (currentArg) {
        result.args.push(currentArg);
        step += 64;
        currentArg = data.slice(step, step + 64);
    }
    return result;
}

JsonLog(decodeInput(i1));
